import React, {useState} from 'react'
import ReactQuill from 'react-quill'
import axios from 'axios'
import 'react-quill/dist/quill.snow.css'
import { useLocation } from 'react-router-dom'


const Write = () => {

  const state = useLocation().state
  const [desc, setDescription]= useState(state?.description || "");
  const [about, setAbout]= useState(state?.about || "");
  const [icon, setIcon]= useState(state?.icon || "");

  const handleClick = async e =>{
    e.preventDefault()
    try {
      state 
        ? await axios.put(`/about/${state.id}`,{
          about,
          description: desc
      })
        : await axios.post(`/about/`,{
          about,
          description: desc
        })
    } catch (err) {
      console.log(err);
    }
  }
  // console.log(desc);
  return (
    <div className="add">
      <div className="content">
        <input type="text" value={about} placeholder='About' onChange={e=>setAbout(e.target.value)}/>
        <input type="text" value={about} placeholder='icon' onChange={e=>setIcon(e.target.value)}/>
        <div className='editorContainer'>
          <ReactQuill className='editor' theme="snow" value={desc} onChange={setDescription} />
        </div>
      </div>
      <div className="menu">
        <div className="item">
            <a className='btn btn-primary rounded-pill py-3 px-5 mt-2' onClick={handleClick}>Publish</a>
            <a className='btn btn-info rounded-pill py-3 px-5 mt-2' href='/about'>Back</a>
          </div>
      </div>
    </div>
  )
}

export default Write
