import React, { useEffect, useState } from "react";
import axios from "axios";

function Team (){
    const [teams, setTeams] = useState([])

    useEffect(()=>{
        const fetchData = async ()=>{
            try{
                const res = await axios.get(`/team/`)
                setTeams(res.data)
            }catch(err){
                console.log(err)
            }
        }
        fetchData()
    })
    return (
        <div class="container-xxl py-6">
            <div class="container">
                <div class="mx-auto text-center wow fadeInUp" data-wow-delay="0.1s" >
                    <div class="d-inline-block border rounded-pill text-primary px-4 mb-3">Our Team</div>
                    <h2 class="mb-5">Meet Our Team Members</h2>
                </div>
                {teams.map((team)=>(team.class==="about" &&
                    <div class="row g-4 text-center">
                        <div class="col-lg-3 col-md-6 wow fadeInUp" data-wow-delay="0.1s">
                            <div class="team-item">
                                <h5>{team.name}</h5>
                                <p class="mb-4">Positon</p>
                                <img class="img-fluid rounded-circle w-100 mb-4" src={team.img} alt="" />
                                <div class="d-flex justify-content-center">
                                    <a class="btn btn-square text-primary bg-white m-1" href={team.fb}><i class="fab fa-facebook-f"></i></a>
                                    <a class="btn btn-square text-primary bg-white m-1" href=""><i class="fab fa-twitter"></i></a>
                                    <a class="btn btn-square text-primary bg-white m-1" href=""><i class="fab fa-linkedin-in"></i></a>
                                </div>
                            </div>
                        </div>
                    </div>
                ))}
            </div>
        </div>
        )}

export default Team
